import React, { useState } from 'react';
import { TextField, Button, Typography, Box, Grid, Link } from '@mui/material';
import { axiosi } from "../config/Axios"; // Assuming axiosi is your axios instance

const ResetPassword = () => {
    const [email, setEmail] = useState('');
    const [error, setError] = useState('');
    const [successMessage, setSuccessMessage] = useState('');

    const handleResetPassword = async (e) => {
        e.preventDefault();
        setError('');
        setSuccessMessage('');

        try {
            const response = await axiosi.post('/auth/reset-password', { email });
            if (response.status === 200) {
                setSuccessMessage('Password reset link sent to your email!');
            }
        } catch (err) {
            setError(err.response?.data?.message || 'Error occurred. Please try again.');
        }
    };

    return (
        <Box
            sx={{
                display: 'flex',
                height: '100vh',
                backgroundImage: 'url(https://source.unsplash.com/random/1600x900?banking)',
                backgroundSize: 'cover',
                backgroundPosition: 'center',
            }}
        >
            <Grid container sx={{ width: '100%' }}>
                {/* Left Section (Password Reset Form) */}
                <Grid item xs={12} md={6} sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', padding: 4 }}>
                    <Box
                        sx={{
                            width: '100%',
                            maxWidth: 400,
                            backgroundColor: 'rgba(255, 255, 255, 0.8)',
                            padding: 4,
                            borderRadius: 3,
                            boxShadow: 5,
                        }}
                    >
                        <Typography variant="h5" sx={{ marginBottom: 2, textAlign: 'center' }}>
                            Reset Your Password
                        </Typography>
                        <form onSubmit={handleResetPassword}>
                            <TextField
                                label="Email"
                                variant="outlined"
                                fullWidth
                                margin="normal"
                                type="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                            {error && (
                                <Typography variant="body2" color="error" sx={{ marginTop: 2 }}>
                                    {error}
                                </Typography>
                            )}
                            {successMessage && (
                                <Typography variant="body2" color="primary" sx={{ marginTop: 2 }}>
                                    {successMessage}
                                </Typography>
                            )}
                            <Button
                                type="submit"
                                variant="contained"
                                color="primary"
                                fullWidth
                                sx={{ marginTop: 2 }}
                            >
                                Send Reset Link
                            </Button>
                        </form>
                        <Typography variant="body2" sx={{ marginTop: 2, textAlign: 'center' }}>
                            Remember your password?{' '}
                            <Link href="/login" underline="hover">
                                Login here
                            </Link>
                        </Typography>
                    </Box>
                </Grid>

                {/* Right Section (Bank Image and Name) */}
                <Grid item xs={12} md={6} sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', padding: 4 }}>
                    <Box
                        sx={{
                            textAlign: 'center',
                            color: 'white',
                            padding: 4,
                            backgroundColor: 'rgba(0,0,0,0.87)',
                            borderRadius: 3,
                        }}
                    >
                        <Typography variant="h2" sx={{ fontWeight: 'bold', marginBottom: 2 }}>
                            Meiri Bank
                        </Typography>
                        <Typography variant="h6" sx={{ marginBottom: 3 }}>
                            Empowering your financial future
                        </Typography>
                        <img
                            src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSS9y-hBJS7leDjIyOeFmOdaIQklTmA60XHhA&s"
                            alt="Bank Logo"
                            style={{
                                width: '80%',
                                maxWidth: 400,
                                borderRadius: 10,
                                boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.2)',
                            }}
                        />
                    </Box>
                </Grid>
            </Grid>
        </Box>
    );
};

export default ResetPassword;
